/**
 * Check if `entity` is `null`.
 *
 * @example
 * ```typescript
 * isNull(null); // true
 * isNull(undefined); // false
 * ```
 * @template Actual Actual type of the entity.
 * @param entity Entity to check.
 * @returns `true` if is null, `false` if not.
 */
export declare const isNull: <Actual = unknown>(
	entity: Actual | null,
) => entity is null;
