import type { ImmutableRecord } from "../types/ImmutableRecord.js";
/**
 * Check if `entity` is an instance of `Object` (`null` is omitted).
 *
 * @example
 * ```typescript
 * isObject({}); // true
 * isObject(null); // false
 * ```
 * @template Actual Actual type of the entity.
 * @param entity Entity to check.
 * @returns `true` if is object, `false` if not.
 */
export declare const isObject: <Actual = unknown>(
	entity: Actual | Readonly<Record<string | number | symbol, unknown>>,
) => entity is Readonly<Record<string | number | symbol, unknown>>;
